\textbf{1)} Given:
\begin{align}
P(A) &= 0.3 \\
P(B) &= 0.4 \\
P(A \cap B) &= 0.2
\end{align}

\textbf{a)} 
\begin{align}
P(A \cup B) &= P(A) + P(B) - P(A \cap B) \\
&= 0.3 + 0.4 - 0.2 \\
&= 0.5
\end{align}

\textbf{b)} If $A$ and $B$ are independent:
\begin{align}
P(A) \cdot P(B) &= P(A \cap B) \\
0.3 \cdot 0.4 &= 0.12 \neq 0.2
\end{align}
Therefore, $A$ and $B$ are not independent.

\textbf{c)} 
\begin{align}
P(A^c \cap B) &= P(B) - P(A \cap B) \\
&= 0.4 - 0.2 \\
&= 0.2
\end{align}

\textbf{2)} Given: $f_X(x) = \begin{cases} 2x & 0 \leq x \leq 1 \\ 0 & \text{otherwise} \end{cases}$

\textbf{a)} 
\begin{align}
E(X) &= \int_{-\infty}^{\infty} x f(x) \, dx \\
&= \int_0^1 x \cdot 2x \, dx \\
&= \int_0^1 2x^2 \, dx \\
&= \frac{2x^3}{3} \Big|_0^1 \\
&= \frac{2}{3}
\end{align}

\begin{align}
\text{Var}(X) &= E(X^2) - [E(X)]^2 \\
E(X^2) &= \int_{-\infty}^{\infty} x^2 f(x) \, dx \\
&= \int_0^1 x^2 \cdot 2x \, dx \\
&= \int_0^1 2x^3 \, dx \\
&= \frac{2x^4}{4} \Big|_0^1 \\
&= \frac{1}{2}
\end{align}

\begin{align}
\text{Var}(X) &= \frac{1}{2} - \left(\frac{2}{3}\right)^2 \\
&= \frac{1}{2} - \frac{4}{9} \\
&= \frac{1}{18}
\end{align}

\textbf{b)} For a Bernoulli distribution: $P(X = x) = p^x(1-p)^{1-x}$

\begin{align}
P(X = 1) &= p^1(1-p)^0 = p \\
P(X = 0) &= p^0(1-p)^1 = (1-p)
\end{align}

\begin{align}
E(X) &= \sum x P(X = x) \\
&= 0(1-p) + 1(p) \\
&= p
\end{align}

\begin{align}
E(X^2) &= \sum x^2 P(X = x) \\
&= 0^2(1-p) + 1^2(p) \\
&= p
\end{align}

\begin{align}
\text{Var}(X) &= E(X^2) - [E(X)]^2 \\
&= p - p^2 \\
&= p(1-p)
\end{align}

