\textbf{3)} Given: $f_{X,Y}(x,y) = 6xy$ for $0 \leq x \leq 1, 0 \leq y \leq 1$

\textbf{a)} 
\begin{align}
f_X(x) &= \int_0^1 6xy \, dy \\
&= 6x \int_0^1 y \, dy \\
&= 6x \cdot \frac{y^2}{2} \Big|_0^1 \\
&= 6x \cdot \frac{1}{2} \\
&= 3x
\end{align}

Similarly, $f_Y(y) = 3y$

\textbf{b)} 
\begin{align}
f_X(x) \cdot f_Y(y) &= 3x \cdot 3y = 9xy \neq 6xy = f_{X,Y}(x,y)
\end{align}
Therefore, $X$ and $Y$ are not independent.

\textbf{4)} Event $A$: sum is 5 or 6, so $P(A) = \frac{9}{36} = \frac{1}{4}$

Event $B$: outcomes $(3,6), (6,6), (3,6), (6,6), (6,3), (3,4), (6,3), (5,4), (4,5), (2,4)$
$P(B) = \frac{10}{36}$

\begin{align}
|A \cap B| &= |(5,4), (5,5), (5,6), (6,3), (6,4), (6,5), (4,6)| = 7 \\
P(B|A) &= \frac{P(A \cap B)}{P(A)} = \frac{7/36}{1/4} = \frac{7/36}{9/36} = \frac{7}{9}
\end{align}

\textbf{5)} Given: $f(x) = 3(1-x)^2$ and $Y = (1-X)^{1/3}$

From $Y^{1/3} = 1-X$, we get $X = 1-Y^{1/3}$

\begin{align}
\frac{dx}{dy} &= -\frac{1}{3}Y^{-2/3}
\end{align}

\begin{align}
f_Y(y) &= f_X(1-y^{1/3}) \left|\frac{dx}{dy}\right| \\
&= 3(1-(1-y^{1/3}))^2 \cdot \frac{1}{3}y^{-2/3} \\
&= 3(y^{1/3})^2 \cdot \frac{1}{3}y^{-2/3} \\
&= 1
\end{align}

Therefore, $f_Y(y) = 1$ for $0 \leq y \leq 1$, and 0 otherwise.

\textbf{6)} For $\text{Cov}\left(\sum_{i=1}^n a_i X_i, \sum_{j=1}^n a_j X_j\right)$:

Let $Y = \sum_{i=1}^n a_i X_i$ and $Z = \sum_{j=1}^n a_j X_j$

\begin{align}
E(Y) &= E\left(\sum_{i=1}^n a_i X_i\right) = \sum_{i=1}^n a_i E(X_i) \\
E(Z) &= \sum_{j=1}^n a_j E(X_j)
\end{align}

\begin{align}
E(YZ) &= E\left(\sum_{i=1}^n a_i X_i \sum_{j=1}^n a_j X_j\right) \\
&= \sum_{i=1}^n \sum_{j=1}^n a_i a_j E(X_i X_j)
\end{align}

\begin{align}
\text{Cov}(Y,Z) &= E(YZ) - E(Y)E(Z) \\
&= \sum_{i=1}^n \sum_{j=1}^n a_i a_j E(X_i X_j) - \sum_{i=1}^n a_i E(X_i) \sum_{j=1}^n a_j E(X_j) \\
&= \sum_{i=1}^n \sum_{j=1}^n a_i a_j [E(X_i X_j) - E(X_i)E(X_j)] \\
&= \sum_{i=1}^n \sum_{j=1}^n a_i a_j \text{Cov}(X_i, X_j)
\end{align}

Since $a^T \Sigma a = \sum_{i=1}^n \sum_{j=1}^n a_i \Sigma_{ij} a_j = \sum_{i=1}^n \sum_{j=1}^n a_i a_j \text{Cov}(X_i, X_j)$

